mapper.insert:
INSERT INTO USER_INFO (
    USER_ID, USER_NAME, PASSWORD, EMAIL, ADDR1, ADDR2, REG_ID, REG_DT, CHG_ID, CHG_DT
)
VALUES (
        #{userId}, #{userName}, #{password}, #{email}, #{addr1}, #{addr2}, #{userId}, SYSDATE, #{userId}, SYSDATE
       )

mapper.select:
SELECT DECODE(COUNT(1), o, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{userID}

SELECT DECODE(COUNT(1), o, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{email}
